import React, {Component} from 'react';
import Concept from './components/Concept/Concept'
import './App.css';
import './font/font.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      mode : 'main',
      clicked : 'clicked'
    }
  }

  render() {
    console.log('App render');
    var _article = null;

    if(this.state.mode === 'main') {
      _article = <Main></Main>;
    }
    else if(this.state.mode === 'speaker'){
      _article = <Speaker></Speaker>
    }
    else if(this.state.mode === 'presenter'){
      _article = <Presenter></Presenter>
    }
    else if(this.state.mode === 'concept')

    return (
      <div className="App">
        <header>
          <Logo onChangeMode={function(_mode){
            this.setState({
              mode:_mode
            });
          }.bind(this)}></Logo>

          <Menu 
            onChangeMode={function(_mode){
              this.setState({
                mode:_mode
              });
            }.bind(this)} clicked = {this.state.clicked}
          ></Menu>
        </header>

        {_article}
      </div>
    );
  }
}


export default App;
